	public static function getCountry($alpha2)
	{
		if(array_key_exists($alpha2, self::$mapping))
		{
			$r = self::$mapping[$alpha2];
			return array(
				'Alpha2' => $alpha2, 
				'Alpha3'=> $r[1],
				'Numeric3'=> $r[0],
				'Name'=> $r[2]
			);
